import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ProductCard = () => {
  const products = [
    {
      id: 1,
      name: "Product 1",
      price: "$10.99",
      description: "This is a sample description for Product 1.",
      image: "https://food.fnr.sndimg.com/content/dam/images/food/fullset/2019/7/11/0/FNK_the-best-pancakes_H_s4x3.jpg.rend.hgtvcom.616.462.suffix/1562853903667.jpeg",
    },
    {
      id: 2,
      name: "Product 2",
      price: "$15.99",
      description: "This is a sample description for Product 2.",
      image: "path_to_image_2.jpg",
    },
    // Add more products as needed
  ];

  return (
    <div>
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-6 mb-3">
            <div className="card" style={{ maxWidth: '400px' }}>
              <div className="row no-gutters">
                {/* Left side - Image */}
                <div className="col-md-4">
                  <img style={{margin:"20px"}}
                    src={product.image}
                    alt="Product"
                    className="card-img"
                  />
                </div>
                {/* Right side - Name, Price, and Description */}
                <div className="col-md-8">
                  <div className="card-body">
                    <div className="d-flex justify-content-between">
                      {/* Product Name */}
                      <h5 className="card-title">{product.name}</h5>
                      {/* Price */}
                      <p className="card-text">{product.price}</p>
                    </div>
                    <hr className="my-1" style={{ borderTop: '1px dotted #ccc' }} />
                    {/* Product Description */}
                    <p className="card-text">{product.description}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductCard;
